export default {
	// model名称
	namespace: '${1:export-model-name}',
	// state
	state: {},
	// 路由监听
	subscriptions: {
		setup({ dispatch, history }) {
			history.listen(location => {});
		}
	},
	// generator 函数
	effects: {
		*init({ payload }, { call, put }) {
			yield put({
				type: 'updateState'
			});
		}
	},
	// reducer
	reducers: {
		// 更新state
		updateState(state, action) {
			return { ...state, ...action.payload };
		}
	}
};
