export default {
	// model名称
	namespace: '${1:export-model-name}',
	// state
	state: {},
	// 路由监听
	subscriptions: {
		setup({ dispatch, history }) {
			history.listen(location => {});
		}
	},
	// generator 函数
	effects: {
		*init({ payload }, { call, put }) {
			yield put({
				type: 'updateState'
			});
		}
	},
	// reducer
	reducers: {
		// 更新state
		updateState(state, action) {
			return { ...state, ...action.payload };
		}
	}
};

export default rootPath => ({
	path: `${rootPath}/${1:url}`,
	getComponent(nextState, cb) {
		require.ensure([], require => {
			cb(null, require("./components/${2:route-component-file-name}"));
		});
	}
});

import qs from 'qs'
import { environments as env } from '@home/constants/constent'
import { utilsFactory } from '@xweb/xweb-utils'

// request example, include GET,POST,DELETE,PUT request template
const request = utilsFactory.getRequest()
// GET
export async function getRequest(params) {
	return request(`${env.host}URL`)
}
// POST
export async function postRequest(params) {
	return request(`${env.host}URL`, {
		method: 'post',
		body: JSON.stringify(params.body)
	})
}
// DELETE
export async function deleteRequest(params) {
return request(`${env.host}URL`, {
		method: 'delete',
		body: qs.stringify(params.body)
	})
}
// PUT
export async function putRequest(params) {
	return request(`${env.host}URL`, {
		method: 'put',
		body: JSON.stringify(params.body)
	})
}
